 1→import { Global, Module } from '@nestjs/common';
 2→import { ConfigService } from '@nestjs/config';
 3→import { createClient, SupabaseClient } from '@supabase/supabase-js';
 4→
 5→export const SUPABASE_CLIENT = 'SUPABASE_CLIENT';
 6→
 7→@Global()
 8→@Module({
 9→  providers: [
10→    {
11→      provide: SUPABASE_CLIENT,
12→      useFactory: (config: ConfigService): SupabaseClient => {
13→        const url = config.getOrThrow<string>('SUPABASE_URL');
14→        const key = config.getOrThrow<string>('SUPABASE_ANON_KEY');
15→        return createClient(url, key, {
16→          auth: { autoRefreshToken: false, persistSession: false },
17→        });
18→      },
19→      inject: [ConfigService],
20→    },
21→  ],
22→  exports: [SUPABASE_CLIENT],
23→})
24→export class SupabaseModule {}
