 2→import { LinksService } from './links.service';
 3→import { SUPABASE_CLIENT } from '../supabase/supabase.module.js';
 4→
 5→const mockSupabaseClient = {
 6→  from: jest.fn().mockReturnThis(),
 7→  select: jest.fn().mockReturnThis(),
 8→  order: jest.fn().mockResolvedValue({ data: [], error: null }),
 9→};
10→
11→describe('LinksService', () => {
12→  let service: LinksService;
13→
14→  beforeEach(async () => {
15→    const module: TestingModule = await Test.createTestingModule({
16→      providers: [
17→        LinksService,
18→        { provide: SUPABASE_CLIENT, useValue: mockSupabaseClient },
19→      ],
20→    }).compile();
21→
22→    service = module.get<LinksService>(LinksService);
23→  });
24→
25→  it('should be defined', () => {
26→    expect(service).toBeDefined();
27→  });
28→
29→  it('findAll should return data from supabase', async () => {
30→    const mockLinks = [
31→      {
32→        id: 1,
33→        url: 'https://example.com',
34→        title: 'Example',
35→        summary: 'A summary',
36→        created_at: new Date().toISOString(),
37→        updated_at: new Date().toISOString(),
38→      },
39→    ];
40→    mockSupabaseClient.order.mockResolvedValueOnce({
41→      data: mockLinks,
42→      error: null,
43→    });
44→
45→    const result = await service.findAll();
46→    expect(result).toEqual(mockLinks);
47→    expect(mockSupabaseClient.from).toHaveBeenCalledWith('links');
48→    expect(mockSupabaseClient.select).toHaveBeenCalledWith('*');
49→    expect(mockSupabaseClient.order).toHaveBeenCalledWith('created_at', {
50→      ascending: false,
51→    });
52→  });
53→
54→  it('findAll should throw on supabase error', async () => {
55→    mockSupabaseClient.order.mockResolvedValueOnce({
56→      data: null,
57→      error: { message: 'db error' },
58→    });
59→
60→    await expect(service.findAll()).rejects.toThrow('db error');
61→  });
62→});
